INSERT INTO exercises (id, lesson_id, test_section_id, title, instruction, content, exercise_type, skill_type, time_limit, max_attempts, passing_score, ordering, is_active, audio_url) VALUES
-- Exercises for new lessons
('130e8400-e29b-41d4-a716-446655440025', 'd50e8400-e29b-41d4-a716-446655440015', NULL, 'Grammar Practice Exercise', 'Complete the sentences with correct grammar', '{"description": "Practice essential grammar rules for IELTS", "passage": "Grammar is fundamental to effective communication in English. Understanding sentence structure, verb tenses, and word order is crucial for achieving a high IELTS score."}', 'lesson', 'reading', 20, 3, 70.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440026', 'd50e8400-e29b-41d4-a716-446655440016', NULL, 'Vocabulary Building Exercise', 'Learn and practice advanced vocabulary', '{"description": "Build your academic vocabulary", "passage": "Academic vocabulary is essential for success in IELTS. Words such as ''analyze'', ''evaluate'', ''demonstrate'', and ''illustrate'' are commonly used in academic contexts and can significantly improve your writing and speaking scores."}', 'lesson', 'reading', 25, 2, 75.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440027', 'd50e8400-e29b-41d4-a716-446655440017', NULL, 'Pronunciation Practice', 'Record and practice pronunciation', '{"description": "Practice pronunciation for IELTS Speaking", "topic": "Pronunciation and stress patterns", "instructions": "Focus on word stress, sentence stress, and intonation patterns. Practice pronouncing difficult sounds and pay attention to connected speech."}', 'lesson', 'speaking', 15, 2, 0.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440028', 'd50e8400-e29b-41d4-a716-446655440018', NULL, 'Reading Time Management', 'Practice reading with time constraints', '{"description": "Improve your reading speed and time management", "passage": "Time management is crucial for IELTS Reading. You have 60 minutes to read three passages and answer 40 questions. Effective strategies include skimming for main ideas, scanning for specific information, and managing your time wisely across all three passages."}', 'lesson', 'reading', 30, 3, 70.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440029', 'd50e8400-e29b-41d4-a716-446655440019', NULL, 'Task 1 Chart Description Practice', 'Describe charts and graphs effectively', '{"description": "Master Task 1 chart descriptions", "chart_url": "/test/image.jpg", "chart_type": "bar", "passage": "The bar chart illustrates the percentage of renewable energy sources used in different countries in 2020. Overall, Germany had the highest percentage at 45%, followed by Denmark at 40%, and the United Kingdom at 35%. Japan had the lowest percentage at 15%. The chart demonstrates the varying levels of renewable energy adoption across different nations."}', 'lesson', 'writing', 60, 1, 0.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440030', 'd50e8400-e29b-41d4-a716-446655440020', NULL, 'Task 2 Essay Writing Practice', 'Write structured essays for Task 2', '{"description": "Practice Task 2 essay writing", "passage": "Some people believe that technology has improved our lives significantly, while others argue that it has created more problems than it has solved. Discuss both views and give your own opinion. Technology has undoubtedly transformed modern society in numerous ways. On one hand, it has revolutionized communication, healthcare, and education. On the other hand, concerns about privacy, job displacement, and social isolation have emerged."}', 'lesson', 'writing', 60, 1, 0.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440031', 'd50e8400-e29b-41d4-a716-446655440021', NULL, 'Reading Question Types Practice', 'Practice all IELTS reading question types', '{"description": "Master all reading question types", "passage": "The passage discusses the importance of biodiversity in maintaining ecosystem balance. Biodiversity refers to the variety of life on Earth, including the diversity of species, genes, and ecosystems. High biodiversity is essential for ecosystem stability, as different species play unique roles in maintaining ecological processes."}', 'lesson', 'reading', 35, 2, 75.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440112', 'd50e8400-e29b-41d4-a716-446655440021', NULL, 'Reading Comprehension Practice', 'Practice reading comprehension with various question types', '{"description": "Improve reading comprehension skills", "passage": "Climate change is one of the most pressing issues facing humanity today. Scientists have documented rising global temperatures, melting ice caps, and increasing sea levels. These changes are primarily driven by human activities, particularly the burning of fossil fuels and deforestation. The consequences of climate change are far-reaching, affecting weather patterns, agriculture, and biodiversity worldwide."}', 'lesson', 'reading', 30, 2, 70.00, 2, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440032', 'd50e8400-e29b-41d4-a716-446655440022', NULL, 'Speaking Part 3 Practice', 'Practice Part 3 discussion skills', '{"description": "Improve Part 3 speaking skills", "topic": "Technology and Education", "instructions": "Discuss abstract topics related to technology and education. Express your opinions, analyze problems, and discuss implications. Provide well-structured answers with examples and reasoning."}', 'lesson', 'speaking', 10, 2, 0.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440033', 'd50e8400-e29b-41d4-a716-446655440023', NULL, 'Listening Section 4 Practice', 'Practice academic lecture listening', '{"description": "Master Section 4 listening skills", "audio_url": "/test/audio.mp3", "audio_duration": 600, "transcript": "Today''s lecture focuses on ancient civilizations and their contributions to modern society. We will explore the development of early human societies, their achievements in architecture, science, and culture, and how these ancient innovations continue to influence our world today."}', 'lesson', 'listening', 30, 2, 70.00, 1, TRUE, '/test/audio.mp3'),
('130e8400-e29b-41d4-a716-446655440034', 'd50e8400-e29b-41d4-a716-446655440024', NULL, 'GT Reading Practice', 'Practice General Training reading', '{"description": "GT reading comprehension practice", "passage": "Looking for a reliable cleaning service? Our company offers professional cleaning services for homes and offices. We have been serving the community for over 10 years with a team of experienced and trained staff. Our services include regular house cleaning, deep cleaning, office maintenance, and specialized cleaning for events."}', 'lesson', 'reading', 30, 3, 70.00, 1, TRUE, NULL),
('130e8400-e29b-41d4-a716-446655440035', 'd50e8400-e29b-41d4-a716-446655440025', NULL, 'GT Writing Task 1 Practice', 'Practice letter writing for GT', '{"description": "Master GT letter writing", "passage": "You recently stayed at a hotel and were not satisfied with the service. Write a letter to the hotel manager explaining what went wrong and what you would like them to do about it. Include details about your stay, the specific problems you encountered, and your expectations for resolution."}', 'lesson', 'writing', 60, 1, 0.00, 1, TRUE, NULL);

